extern int *__bss_begin;                //  start of .bss sectionextern int *__bss_end;                  //  end of .bss sectionextern int *__stack_begin;              //  start of stack sectionextern int *__stack_end;                //  end of stack sectionextern int *__alt_stack_begin;          //  start of alt stack sectionextern int *__alt_stack_end;            //  end of alt stack section/*  forward references  */extern void main(void);/*  prototypes  */asm void __start(void);asm void __ExitProcess(void);/* *  _start  - program entry point */asm void __start(void){   // just to make "__stack_end" appear in final ELF  lrw     r7, __stack_end  // initialize stack pointer  lrw     r10, __stack_begin  mov     r0, r10  // initialize the alternate stack pointer  mfcr     r2, cr0  bseti     r2, 1  mtcr     r2, cr0  lrw     r10, __alt_stack_begin  mov     r0, r10  mfcr     r2, cr0  bclri     r2, 1  mtcr     r2, cr0  // zero out .bss section  lrw     r5, __bss_begin  lrw     r6, __bss_end  cmphs   r5, r6  bt      _end_zero_bss  xor     r4, r4_zero_bss:  subi    r6, 1  st.b    r4, (r6,0)  cmplt   r5, r6  bt      _zero_bss_end_zero_bss:  //  program entry point  bsr   main  //  shut down  jmpi  __ExitProcess}/* *  __ExitProcess - program end point * */asm void __ExitProcess(void){_break:  bkpt  bkpt  br _break}